import React from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import GameCard from './GameCard';

const games = [
  { title: "Horizon Forbidden West", image: "/path/to/horizon.jpg", platform: "PS5", price: 51 },
  { title: "Marvel's Spider-Man", image: "/path/to/spiderman.jpg", platform: "PS5", price: 51 },
  { title: "Little Nightmares 2", image: "/path/to/little-nightmares.jpg", platform: "PS4", price: 40 },
  { title: "Ghost of Tsushima", image: "/path/to/ghost.jpg", platform: "PS4", price: 48 },
];

const GameStore = () => (
  <div className="game-store">
    <Header />
    <div className="content">
      <Sidebar />
      <main>
        <div className="featured-game">
          <img src="/path/to/horizon.jpg" alt="Horizon Forbidden West" />
        </div>
        <div className="game-list">
          {games.map(game => (
            <GameCard key={game.title} game={game} />
          ))}
        </div>
      </main>
    </div>
  </div>
);

export default GameStore;
